","keccak256":"0x32c202bd28995dd20c4347b7c6467a6d3241c74c8ad3edcbb610cd9205916c45","license":"MIT"},"contracts/ArtDrive.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/MerkleProofUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721EnumerableUpgradeable.sol";

import "./utils/Array.sol";
import "./utils/Random.sol";
import "./lib/ArtDriveEvent.sol";
import "./interface/IThesaurus.sol";

contract ArtDrive is ArtDriveEvent, OwnableUpgradeable, ERC721EnumerableUpgradeable {
    using CountersUpgradeable for CountersUpgradeable.Counter;
    using SafeMathUpgradeable for uint256;

    CountersUpgradeable.Counter private _tokenIds;

    uint8 private constant GREY_TYPE = 1;
    uint8 private constant BLUE_TYPE = 2;
    uint8 private constant ORANGE_TYPE = 3;

    IThesaurus public thesaurus;
    string public baseURI;
    uint256 public orangeAmount;
    uint256 public blueAmount;
    uint256 public greyAmount;
    uint256 public price;
    bytes32 public merkleRoot;
    uint256 public defAllowedMintAmount;

    //tokenId => verb content
    mapping(uint256 => string) public verb;

    //tokenId => adj content
    mapping(uint256 => string) public adj;

    //tokenId => noun content
    mapping(uint256 => string) public noun;

    //tokenId => whether to add words
    mapping(uint256 => bool) public added;

    //tokenId => Is it locked
    mapping(uint256 => bool) public locked;

    //tokenId => driver type
    mapping(uint256 => uint8) public types;

    //address => Number of additional mint allowed
    mapping(address => uint256) public allowedMintAmount;

    constructor(
        string memory _name,
        string memory _symbol,
        string memory _baseURI,
        address _thesaurus,
        uint256 _price,
        uint256 _orangeAmount,
        uint256 _blueAmount,
        uint256 _greyAmount,
        uint256 _defAllowedMintAmount,
        bytes32 _merkleRoot
    ) {
        initialize(
            _name,
            _symbol,
            _baseURI,
            _thesaurus,
            _price,
            _orangeAmount,
            _blueAmount,
            _greyAmount,
            _defAllowedMintAmount,
            _merkleRoot
        );
    }

    function initialize(
        string memory _name,
        string memory _symbol,
        string memory _baseURI,
        address _thesaurus,
        uint256 _price,
        uint256 _orangeAmount,
        uint256 _blueAmount,
        uint256 _greyAmount,
        uint256 _defAllowedMintAmount,
        bytes32 _merkleRoot
    ) public initializer {
        __ERC721_init(_name, _symbol);
        __Ownable_init();
        baseURI = _baseURI;
        thesaurus = IThesaurus(_thesaurus);
        price = _price;
        orangeAmount = _orangeAmount;
        blueAmount = _blueAmount;
        greyAmount = _greyAmount;
        defAllowedMintAmount = _defAllowedMintAmount;
        merkleRoot = _merkleRoot;
    }

    // ============= view function =============

    function tokenURI(uint256 _tokenId) public view override returns (string memory) {
        require(_exists(_tokenId), "ArtDrive: URI query for nonexistent token");

        string memory _verb = verb[_tokenId];
        string memory _adj = adj[_tokenId];
        string memory _noun = noun[_tokenId];
        uint8 _type = types[_tokenId];
        return string(abi.encodePacked(baseURI, "/", _type, "/", _verb, "/", _adj, "/", _noun));
    }

    function verifyProof(
        bytes32[] memory _proof,
        address _recipient,
        uint256 _amount
    ) public view returns (bool) {
        bytes32 _data = keccak256(abi.encode(_recipient, _amount));
        return MerkleProofUpgradeable.verify(_proof, merkleRoot, _data);
    }

    // ============= write function =============

    function mint(address _player, uint256 _amount) external returns (uint256[] memory) {
        uint256[] memory tokensId = new uint256[](_amount);
        for (uint256 i = 0; i < _amount; i++) {
            tokensId[i] = _mint(_player);
        }
        return tokensId;
    }

    function refreshWords(uint256 _tokenId) external {
        require(_exists(_tokenId), "ArtDrive: URI query for nonexistent token");
        require(locked[_tokenId], "ArtDrive: token is locked");

        (string memory _verb, string memory _adj, string memory _noun) = thesaurus.randomWords(totalSupply());
        verb[_tokenId] = _verb;
        adj[_tokenId] = _adj;
        noun[_tokenId] = _noun;

        emit RefreshWords(_tokenId, _verb, _adj, _noun);
    }

    function lock(uint256 _tokenId) external {
        require(_exists(_tokenId), "ArtDrive: URI query for nonexistent token");
        require(locked[_tokenId], "ArtDrive: token is locked");
        require(ownerOf(_tokenId) == msg.sender, "ArtDrive: You are not owner");

        locked[_tokenId] = true;
        emit Locked(_tokenId);
    }

    function addWord(
        uint256 _tokenId,
        uint8 _type,
        string memory _word
    ) external {
        require(_exists(_tokenId), "ArtDrive: URI query for nonexistent token");
        require(ownerOf(_tokenId) == msg.sender, "ArtDrive: You are not owner");
        require(added[_tokenId], "ArtDrive: token is added");
        require(bytes(_word).length < 30 && bytes(_word).length > 0, "ArtDrive: word minimum 0, maximum 30 characters");

        uint256 _weight;
        if (types[_tokenId] == GREY_TYPE) {
            _weight = 1;
        } else if (types[_tokenId] == BLUE_TYPE) {
            _weight = 5;
        } else if (types[_tokenId] == ORANGE_TYPE) {
            _weight = thesaurus.totalWordsAmount().div(100);
        } else {
            require(
                false,
                string(abi.encodePacked("ArtDrive: There is no such type [", Strings.toString(_type), " ]"))
            );
        }

        thesaurus.addWord(_type, _weight, _word);
        emit AddWord(_tokenId, _weight, _word);
    }

    function claimMintAmount(
        address _recipient,
        bytes32[] memory _proof,
        uint256 _amount
    ) external {
        require(allowedMintAmount[_recipient] == 0, "ArtDrive: Already received");
        require(verifyProof(_proof, _recipient, _amount), "ArtDrive: The proof could not be verified.");

        allowedMintAmount[_recipient] = _amount;
        emit ClaimMintAmount(_recipient, _amount);
    }

    // ============= owner function =============

    function setDefAllowedMintAmount(uint256 _amount) external onlyOwner {
        uint256 _old = defAllowedMintAmount;
        defAllowedMintAmount = _amount;
        emit NewDefAllowedMintAmount(_old, _amount);
    }

    function setPrice(uint256 _price) external onlyOwner {
        uint256 _old = price;
        price = _price;
        emit NewPrice(_old, _price);
    }

    function transferEth(address _recipient, uint256 _amount) external payable onlyOwner {
        payable(_recipient).transfer(_amount);
        emit TransferEth(_recipient, _amount);
    }

    // ============= internal function =============

    function _mint(address _player) internal returns (uint256 _newTokenId) {
        if (msg.sender != owner()) {
            require(
                balanceOf(_player) < (defAllowedMintAmount.add(allowedMintAmount[_player])),
                "ArtDrive: Exceeds the maximum number of mint"
            );

            if (balanceOf(_player) >= allowedMintAmount[_player]) {
                require(msg.value >= price, "ArtDrive: msg.value Deficiency");
            }
        }
        _newTokenId = _tokenIds.current();

        //words
        (string memory _verb, string memory _adj, string memory _noun) = thesaurus.randomWords(totalSupply());
        verb[_newTokenId] = _verb;
        adj[_newTokenId] = _adj;
        noun[_newTokenId] = _noun;

        //type
        uint8 _type = _randomType(_newTokenId);
        types[_newTokenId] = _type;

        _mint(_player, _newTokenId);

        _tokenIds.increment();
        emit Mint(_newTokenId, _player, _verb, _adj, _noun, _type);
    }

    function _randomType(uint256 _tokenId) private view returns (uint8) {
        uint8[] memory _list = new uint8[](3);
        if (greyAmount > 0) _list[0] = GREY_TYPE;
        if (blueAmount > 0) _list[1] = BLUE_TYPE;
        if (orangeAmount > 0) _list[2] = ORANGE_TYPE;

        _list = Array.removeEmpty(_list);

        uint8 _random = Random.random8(0, _list.length, _tokenId);
        return _list[_random];
    }

    // ============= base function =============

    fallback() external payable {}

    receive() external payable {}
}
","keccak256":"0xd6e9eef821f477f139ee7e194cd74425637b9c5021e53b28c3b8af3d7a115ee5","license":"MIT"},"contracts/interface/IThesaurus.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

interface IThesaurus {
    function addWord(
        uint8 _type,
        uint256 _weight,
        string memory _content
    ) external;

    function totalWordsAmount() external view returns (uint256);

    function randomWords(uint256)
        external
        view
        returns (
            string memory _verb,
            string memory _adj,
            string memory _noun
        );

    function VERB_TYPE() external pure returns (uint8);

    function ADJ_TYPE() external pure returns (uint8);

    function NOUN_TYPE() external pure returns (uint8);
}
","keccak256":"0x4cd2f85b67018e957e9e62be6c2eb998aa34fc5021598e12bf48939a1abe1864","license":"MIT"},"contracts/lib/ArtDriveEvent.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

abstract contract ArtDriveEvent {
    event Mint(uint256 tokenId, address player, string verb, string adj, string noun, uint8 types);
    event RefreshWords(uint256 tokenId, string verb, string adj, string noun);
    event Locked(uint256 tokenId);
    event AddWord(uint256 tokenId, uint256 weight, string word);
    event NewDefAllowedMintAmount(uint256 _old, uint256 _new);
    event NewPrice(uint256 _old, uint256 _new);
    event TransferEth(address _recipient, uint256 _amount);
    event ClaimMintAmount(address _recipient, uint256 _amount);
}
","keccak256":"0x31284a83fd7f6b6260541a671859eeac9785882b0a391f98f28bc8171fcb25c6","license":"MIT"},"contracts/utils/Array.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

library Array {
    function removeEmpty(uint8[] memory b) internal pure returns (uint8[] memory) {
        uint256 count;
        for (uint256 i = 0; i < b.length; i++) {
            if (b[i] != uint8(0)) {
                count++;
            }
        }
        uint8[] memory a = new uint8[](count);
        uint256 j;
        for (uint256 i = 0; i < b.length; i++) {
            if (b[i] == uint8(0)) {
                continue;
            }
            a[j] = b[i];
            j++;
        }
        return a;
    }
}
","keccak256":"0xc5a10483848b21ef8a4afc7dc79f07a97cefffd5038f027c6f69c5d800e66b94","license":"MIT"},"contracts/utils/Random.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

library Random {
    function random(
        uint256 _from,
        uint256 _to,
        uint256 _nonce
    ) internal view returns (uint256) {
        uint256 randNonce = uint256(keccak256(abi.encodePacked(block.timestamp, block.difficulty, _nonce)));
        return (uint256(keccak256(abi.encodePacked(block.timestamp, block.difficulty, randNonce))) % _to) + _from;
    }

    function random8(
        uint256 _from,
        uint256 _to,
        uint256 _nonce
    ) internal view returns (uint8) {
        return uint8(random(_from, _to, _nonce));
    }
}
